mapper.select:
select 

,

from send_mail_queue
where send_mail_queue_id = #{sendMailQueueId,jdbcType=BIGINT}
  and created_at = #{createdAt,jdbcType=TIMESTAMP}

mapper.delete:
delete from send_mail_queue
where send_mail_queue_id = #{sendMailQueueId,jdbcType=BIGINT}
  and created_at = #{createdAt,jdbcType=TIMESTAMP}
